### Correlation ID Propagation

```typescript
// apps/gateway/src/middleware/correlation.middleware.ts

import { v4 as uuidv4 } from 'uuid';
import { AsyncLocalStorage } from 'async_hooks';

// AsyncLocalStorage for request-scoped correlation
const correlationStorage = new AsyncLocalStorage<CorrelationContext>();

interface CorrelationContext {
  correlationId: string;
  parentSpanId?: string;
  traceId?: string;
  requestId: string;
}

export function correlationMiddleware(req: Request, res: Response, next: NextFunction) {
  // Extract or generate correlation ID
  const correlationId = 
    req.headers['x-correlation-id'] as string ||
    req.headers['x-request-id'] as string ||
    uuidv4();

  const context: CorrelationContext = {
    correlationId,
    traceId: req.headers['x-trace-id'] as string,
    parentSpanId: req.headers['x-parent-span-id'] as string,
    requestId: uuidv4(),
  };

  // Set response header for client correlation
  res.setHeader('x-correlation-id', correlationId);
  res.setHeader('x-request-id', context.requestId);

  // Run request in correlation context
  correlationStorage.run(context, () => next());
}

export function getCorrelationContext(): CorrelationContext | undefined {
  return correlationStorage.getStore();
}

// Propagate to external service calls
export function getCorrelationHeaders(): Record<string, string> {
  const context = getCorrelationContext();
  if (!context) return {};

  return {
    'x-correlation-id': context.correlationId,
    'x-request-id': context.requestId,
    'x-trace-id': context.traceId || context.correlationId,
  };
}
```

### Sensitive Data Redaction

```typescript
// apps/gateway/src/services/audit-redaction.service.ts

interface RedactionConfig {
  // Fields to completely remove
  removeFields: string[];

  // Fields to mask (show partial)
  maskFields: {
    field: string;
    pattern: 'email' | 'phone' | 'card' | 'ssn' | 'api_key' | 'custom';
    customMask?: (value: string) => string;
  }[];

  // Fields to hash (for later matching without exposing)
  hashFields: string[];

  // Regex patterns to redact in any string field
  redactPatterns: RegExp[];
}

const defaultRedactionConfig: RedactionConfig = {
  removeFields: [
    'password',
    'passwordHash',
    'secret',
    'privateKey',
    'accessToken',
    'refreshToken',
    'sessionToken',
    'creditCard',
    'cvv',
    'ssn',
  ],

  maskFields: [
    { field: 'email', pattern: 'email' },  // j***@example.com
    { field: 'phone', pattern: 'phone' },  // ***-***-1234
    { field: 'apiKey', pattern: 'api_key' }, // sk_...***xyz
  ],

  hashFields: [
    'userId', // Can verify matches without exposing
  ],

  redactPatterns: [
    /Bearer\s+[A-Za-z0-9\-._~+\/]+=*/g,  // JWT tokens
    /sk_[a-zA-Z0-9]{32,}/g,              // API keys
  ],
};

class AuditRedactionService {
  redact(data: any, config = defaultRedactionConfig): any {
    if (!data) return data;
    if (typeof data === 'string') return this.redactString(data, config);
    if (Array.isArray(data)) return data.map(item => this.redact(item, config));
    if (typeof data === 'object') return this.redactObject(data, config);
    return data;
  }

  private applyMask(value: string, pattern: string): string {
    switch (pattern) {
      case 'email':
        const [local, domain] = value.split('@');
        return local[0] + '***@' + domain;
      case 'phone':
        return value.replace(/\d(?=\d{4})/g, '*');
      case 'api_key':
        return value.slice(0, 5) + '***' + value.slice(-3);
      default:
        return '***';
    }
  }
}
