```typescript
// apps/gateway/src/services/audit-clickhouse.service.ts

interface ClickHouseConfig {
  host: string;
  port: number;
  database: string;
  username: string;
  password: string;
  cluster?: string;
}

class AuditClickHouseService {
  async forwardEvent(event: AuditEvent): Promise<void> {
    // Buffer events for batch insert
    this.buffer.push(this.transformForClickHouse(event));

    if (this.buffer.length >= this.batchSize || this.shouldFlush()) {
      await this.flushBuffer();
    }
  }

  private transformForClickHouse(event: AuditEvent): ClickHouseRow {
    return {
      event_id: event.id,
      correlation_id: event.correlationId,
      timestamp: event.timestamp,
      org_id: event.org.id,
      actor_type: event.actor.type,
      actor_id: event.actor.id,
      action: event.action,
      resource_type: event.resource.type,
      resource_id: event.resource.id,
      status: event.operation.status,
      duration_ms: event.duration || 0,
      metadata: JSON.stringify(event.metadata),
      // Materialized columns for common queries
      date: event.timestamp.toISOString().split('T')[0],
      hour: event.timestamp.getUTCHours(),
    };
  }
}
