  // Maintenance operations
  CHECKPOINT_COMPACT = 'checkpoint_compact',
  CACHE_WARM = 'cache_warm'
}

enum JobStatus {
  PENDING = 'pending',           // Queued, waiting to start
  RUNNING = 'running',           // Currently executing
  PAUSED = 'paused',             // Temporarily paused
