### Job Queue

```typescript
interface JobQueueConfig {
  // Concurrency limits
  concurrency: {
    global: number;              // Max concurrent jobs overall
    perType: Record<JobType, number>;  // Max per job type
    perSession: number;          // Max per session
  };

  // Timeouts
  timeouts: {
    default: number;             // Default timeout (5 minutes)
    perType: Record<JobType, number>;  // Per-type overrides
  };

  // Retry
  retry: {
    maxAttempts: number;         // Default: 3
    backoffMultiplier: number;   // Default: 2
    initialBackoffMs: number;    // Default: 1000
    maxBackoffMs: number;        // Default: 60000
  };

  // Cleanup
  cleanup: {
    completedRetentionHours: number;  // Keep completed jobs for N hours
    failedRetentionHours: number;     // Keep failed jobs for N hours
  };
}

class JobQueue {
  private queue: PriorityQueue<Job>;
  private running = new Map<string, JobExecution>();
  private handlers = new Map<JobType, JobHandler>();

  constructor(
    private config: JobQueueConfig,
    private storage: JobStorage,
    private events: EventEmitter
  ) {}

  async enqueue(job: Omit<Job, 'id' | 'status' | 'createdAt'>): Promise<Job> {
    const newJob: Job = {
      ...job,
      id: ulid(),
      status: JobStatus.PENDING,
      createdAt: new Date(),
      progress: {
        current: 0,
        total: 100,
        percentage: 0,
        message: 'Queued'
      },
      retry: {
        attempts: 0,
        maxAttempts: this.config.retry.maxAttempts,
        backoffMs: this.config.retry.initialBackoffMs
      }
    };

    await this.storage.save(newJob);
    this.queue.enqueue(newJob, newJob.priority);

    this.events.emit('job.created', { jobId: newJob.id, type: newJob.type });
    this.processQueue();

    return newJob;
  }

  private async processQueue(): Promise<void> {
    while (this.canStartJob() && !this.queue.isEmpty()) {
      const job = this.queue.dequeue();
      if (job && this.canRunJob(job)) {
        this.startJob(job);
      }
    }
  }

  private canStartJob(): boolean {
    return this.running.size < this.config.concurrency.global;
  }

  private canRunJob(job: Job): boolean {
    // Check per-type limit
    const typeCount = Array.from(this.running.values())
      .filter(e => e.job.type === job.type).length;
    const typeLimit = this.config.concurrency.perType[job.type] || 
                      this.config.concurrency.global;
    if (typeCount >= typeLimit) return false;

    // Check per-session limit
    if (job.sessionId) {
      const sessionCount = Array.from(this.running.values())
        .filter(e => e.job.sessionId === job.sessionId).length;
      if (sessionCount >= this.config.concurrency.perSession) return false;
    }

    return true;
  }

  private async startJob(job: Job): Promise<void> {
    const handler = this.handlers.get(job.type);
    if (!handler) {
      await this.failJob(job, {
        code: 'NO_HANDLER',
        message: `No handler registered for job type: ${job.type}`,
        retryable: false
      });
      return;
    }

    job.status = JobStatus.RUNNING;
    job.startedAt = new Date();
    await this.storage.save(job);

    this.events.emit('job.started', { jobId: job.id, type: job.type });

    const execution = new JobExecution(job, handler, this);
    this.running.set(job.id, execution);

    execution.run()
      .then(() => this.completeJob(job))
      .catch(error => this.handleJobError(job, error))
      .finally(() => {
        this.running.delete(job.id);
        this.processQueue();
      });
  }
}
```

### Job Handler Interface

```typescript
interface JobHandler<TInput = any, TOutput = any> {
  // Validate input before execution
  validate(input: TInput): Promise<ValidationResult>;

  // Execute the job
  execute(context: JobContext<TInput>): Promise<TOutput>;

  // Optional: Handle cancellation cleanup
  onCancel?(context: JobContext<TInput>): Promise<void>;

  // Optional: Handle pause
  onPause?(context: JobContext<TInput>): Promise<void>;

  // Optional: Handle resume
  onResume?(context: JobContext<TInput>): Promise<void>;
}

interface JobContext<TInput> {
  job: Job;
  input: TInput;

  // Progress reporting
  updateProgress(current: number, total: number, message?: string): Promise<void>;
  setStage(stage: string): Promise<void>;

  // Checkpointing for resume
  checkpoint(state: any): Promise<void>;
  getCheckpoint(): Promise<any | null>;

  // Cancellation checking
  isCancelled(): boolean;
  throwIfCancelled(): void;

  // Logging
  log(level: 'debug' | 'info' | 'warn' | 'error', message: string): void;
}

// Example handler implementation
class CodebaseScanHandler implements JobHandler<ScanInput, ScanOutput> {
  async validate(input: ScanInput): Promise<ValidationResult> {
    if (!input.path) {
      return { valid: false, errors: ['path is required'] };
    }
    return { valid: true, errors: [] };
  }

  async execute(context: JobContext<ScanInput>): Promise<ScanOutput> {
    const { input } = context;

    // Get files to scan
    const files = await this.getFiles(input.path, input.patterns);

    context.setStage('scanning');
    const results: ScanResult[] = [];

    for (let i = 0; i < files.length; i++) {
      // Check for cancellation
      context.throwIfCancelled();

      // Update progress
      await context.updateProgress(i, files.length, `Scanning ${files[i]}`);

      // Scan file
      const result = await this.scanFile(files[i]);
      results.push(result);

      // Checkpoint every 100 files
      if (i % 100 === 0) {
        await context.checkpoint({ lastIndex: i, results });
      }
    }

    return { results, totalFiles: files.length };
  }

  async onCancel(context: JobContext<ScanInput>): Promise<void> {
    // Cleanup partial results
    context.log('info', 'Scan cancelled, cleaning up');
  }
}
```

### Job Execution

```typescript
class JobExecution {
  private cancelled = false;
  private timeout: NodeJS.Timeout | null = null;

  constructor(
    readonly job: Job,
    private handler: JobHandler,
    private queue: JobQueue
  ) {}

  async run(): Promise<void> {
    const timeoutMs = this.queue.getTimeout(this.job.type);

    // Set timeout
    this.timeout = setTimeout(() => {
      this.handleTimeout();
    }, timeoutMs);

    try {
      const context = this.createContext();
      const output = await this.handler.execute(context);

      this.job.output = output;
      this.job.status = JobStatus.COMPLETED;
      this.job.completedAt = new Date();

    } finally {
      if (this.timeout) {
        clearTimeout(this.timeout);
      }
    }
  }

  cancel(reason?: string): void {
    this.cancelled = true;
    this.job.cancellation = {
      requestedAt: new Date(),
      requestedBy: 'user',
      reason
    };

    if (this.handler.onCancel) {
      this.handler.onCancel(this.createContext());
    }
  }

  private createContext(): JobContext<any> {
    return {
      job: this.job,
      input: this.job.input,

      updateProgress: async (current, total, message) => {
        this.job.progress = {
          current,
          total,
          percentage: Math.round((current / total) * 100),
          message: message || this.job.progress.message,
          stage: this.job.progress.stage
        };
        await this.queue.storage.save(this.job);
        this.queue.events.emit('job.progress', {
          jobId: this.job.id,
          progress: this.job.progress
        });
      },

      setStage: async (stage) => {
        this.job.progress.stage = stage;
        await this.queue.storage.save(this.job);
      },

      checkpoint: async (state) => {
        this.job.metadata.checkpoint = state;
        await this.queue.storage.save(this.job);
      },

      getCheckpoint: async () => {
        return this.job.metadata.checkpoint || null;
      },

      isCancelled: () => this.cancelled,

      throwIfCancelled: () => {
        if (this.cancelled) {
          throw new JobCancelledException(this.job.id);
        }
      },

      log: (level, message) => {
        this.queue.logger[level](`[Job ${this.job.id}] ${message}`);
      }
    };
  }
}
