    s2p: { budget: number; enabled: boolean };
  };
  agentType: 'claude' | 'codex' | 'gemini';
  taskDescription: string;
}

interface ContextPack {
  id: string;
  createdAt: Date;
  config: ContextPackConfig;
  sections: {
    triage?: string;
    memory?: string;
