## Checksum Generation Script
```typescript
// scripts/generate-checksums.ts
import { createHash } from "crypto";
import { readFile, writeFile, readdir } from "fs/promises";
import { join } from "path";

interface ChecksumEntry {
  filename: string;
  sha256: string;
  sha512: string;
  size: number;
}

async function generateChecksums(directory: string): Promise<ChecksumEntry[]> {
  const files = await readdir(directory);
  const artifacts = files.filter(f => 
    f.endsWith(".tar.gz") || f.endsWith(".zip") || f.endsWith(".exe")
  );

  const entries: ChecksumEntry[] = [];

  for (const filename of artifacts) {
    const filepath = join(directory, filename);
    const content = await readFile(filepath);

    const sha256 = createHash("sha256").update(content).digest("hex");
    const sha512 = createHash("sha512").update(content).digest("hex");

    entries.push({
      filename,
      sha256,
      sha512,
      size: content.length,
    });

    // Write individual checksum files
    await writeFile(
      join(directory, `${filename}.sha256`),
      `${sha256}  ${filename}\n`
    );
    await writeFile(
      join(directory, `${filename}.sha512`),
      `${sha512}  ${filename}\n`
    );

    console.log(`Generated checksums for ${filename}`);
  }

  // Write combined checksums file (SHA256)
  const checksumsTxt = entries
    .map(e => `${e.sha256}  ${e.filename}`)
    .join("\n") + "\n";
  await writeFile(join(directory, "checksums.txt"), checksumsTxt);

  // Write JSON manifest for programmatic access
  const manifest = {
    version: process.env.VERSION || "unknown",
    generated: new Date().toISOString(),
    files: entries,
  };
  await writeFile(
    join(directory, "checksums.json"),
    JSON.stringify(manifest, null, 2)
  );

  return entries;
}

// Run
generateChecksums(process.argv[2] || "dist").catch(console.error);
```

## Update Checker Service
```typescript
// apps/gateway/src/services/update-checker.service.ts
import { createHash } from "crypto";
import { readFile, writeFile } from "fs/promises";

interface ReleaseInfo {
  version: string;
  publishedAt: string;
  assets: {
    name: string;
    downloadUrl: string;
    size: number;
    sha256: string;
  }[];
  changelog: string;
}

interface UpdateCheckResult {
  currentVersion: string;
  latestVersion: string;
  updateAvailable: boolean;
  release?: ReleaseInfo;
}

export class UpdateCheckerService {
  private readonly owner = "Dicklesworthstone";
  private readonly repo = "flywheel_gateway";
  private readonly currentVersion: string;
  private readonly cacheFile = ".update-cache.json";
  private readonly cacheTTL = 24 * 60 * 60 * 1000; // 24 hours

  constructor() {
    this.currentVersion = process.env.VERSION || "0.0.0";
  }

  async checkForUpdates(): Promise<UpdateCheckResult> {
    // Check cache first
    const cached = await this.getCachedResult();
    if (cached) return cached;

    const release = await this.fetchLatestRelease();
    const result: UpdateCheckResult = {
      currentVersion: this.currentVersion,
      latestVersion: release.version,
      updateAvailable: this.isNewerVersion(release.version),
      release: this.isNewerVersion(release.version) ? release : undefined,
    };

    await this.cacheResult(result);
    return result;
  }

  private async fetchLatestRelease(): Promise<ReleaseInfo> {
    const response = await fetch(
      `https://api.github.com/repos/${this.owner}/${this.repo}/releases/latest`,
      {
        headers: {
          Accept: "application/vnd.github.v3+json",
          "User-Agent": `flywheel-gateway/${this.currentVersion}`,
        },
      }
    );

    if (!response.ok) {
      throw new Error(`Failed to fetch release: ${response.statusText}`);
    }

    const data = await response.json();

    // Fetch checksums.json from release assets
    const checksumsAsset = data.assets.find(
      (a: any) => a.name === "checksums.json"
    );
    let checksums: Record<string, string> = {};

    if (checksumsAsset) {
      const checksumsResponse = await fetch(checksumsAsset.browser_download_url);
      const checksumsData = await checksumsResponse.json();
      checksums = Object.fromEntries(
        checksumsData.files.map((f: any) => [f.filename, f.sha256])
      );
    }

    return {
      version: data.tag_name.replace(/^v/, ""),
      publishedAt: data.published_at,
      assets: data.assets
        .filter((a: any) => a.name.endsWith(".tar.gz") || a.name.endsWith(".zip"))
        .map((a: any) => ({
          name: a.name,
          downloadUrl: a.browser_download_url,
          size: a.size,
          sha256: checksums[a.name] || "",
        })),
      changelog: data.body,
    };
  }

  async downloadAndVerify(asset: ReleaseInfo["assets"][0], destPath: string): Promise<void> {
    console.log(`Downloading ${asset.name}...`);

    const response = await fetch(asset.downloadUrl);
    if (!response.ok) {
      throw new Error(`Download failed: ${response.statusText}`);
    }

    const buffer = await response.arrayBuffer();
    const content = Buffer.from(buffer);

    // Verify checksum BEFORE writing to disk
    const actualHash = createHash("sha256").update(content).digest("hex");

    if (asset.sha256 && actualHash !== asset.sha256) {
      throw new Error(
        `Checksum mismatch!\n` +
        `Expected: ${asset.sha256}\n` +
        `Actual:   ${actualHash}\n` +
        `This could indicate a corrupted download or tampered file.`
      );
    }

    console.log(`Checksum verified: ${actualHash}`);
    await writeFile(destPath, content);
    console.log(`Downloaded to ${destPath}`);
  }

  private isNewerVersion(latest: string): boolean {
    const current = this.currentVersion.split(".").map(Number);
    const release = latest.split(".").map(Number);

    for (let i = 0; i < 3; i++) {
      if ((release[i] || 0) > (current[i] || 0)) return true;
      if ((release[i] || 0) < (current[i] || 0)) return false;
    }
    return false;
  }

  // Cache methods...
}
