const updateChecker = new UpdateCheckerService();

async function main() {
  const command = process.argv[2];

  switch (command) {
    case "check":
      const result = await updateChecker.checkForUpdates();
      if (result.updateAvailable) {
        console.log(`\nUpdate available: ${result.latestVersion}`);
        console.log(`Current version: ${result.currentVersion}`);
        console.log(`\nRun 'flywheel-update download' to download`);
      } else {
        console.log(`You're on the latest version (${result.currentVersion})`);
      }
      break;

    case "download":
      const { release } = await updateChecker.checkForUpdates();
      if (!release) {
        console.log("No updates available");
        return;
      }

      // Find appropriate asset for current platform
      const platform = `${process.platform}-${process.arch}`;
      const asset = release.assets.find(a => a.name.includes(platform));

      if (!asset) {
        console.error(`No release asset found for ${platform}`);
        return;
      }

      await updateChecker.downloadAndVerify(asset, `./flywheel-${release.version}.tar.gz`);
      console.log("\nDownload complete. Verify and install manually.");
      break;

    default:
      console.log("Usage: flywheel-update [check|download]");
  }
}
