### 2. Create Schema Registry
```typescript
// apps/gateway/src/api/schemas.ts

import { extendZodWithOpenApi, OpenAPIRegistry } from "@asteasolutions/zod-to-openapi";
import { z } from "zod";

extendZodWithOpenApi(z);

export const registry = new OpenAPIRegistry();

// Register schemas
export const SpawnAgentRequestSchema = z.object({
  workingDirectory: z.string().min(1).openapi({
    description: "Absolute path to the agent working directory",
    example: "/home/user/project",
  }),
  agentId: z.string().optional().openapi({
    description: "Custom agent ID (auto-generated if not provided)",
  }),
  systemPrompt: z.string().optional().openapi({
    description: "System prompt for the agent",
  }),
  timeout: z.number().min(1000).max(86400000).optional().openapi({
    description: "Timeout in milliseconds",
    default: 300000,
  }),
}).openapi("SpawnAgentRequest");

registry.register("SpawnAgentRequest", SpawnAgentRequestSchema);

// Response schemas
export const AgentResponseSchema = z.object({
  agentId: z.string(),
  state: z.enum(["spawning", "ready", "busy", "terminated"]),
  driver: z.string(),
  createdAt: z.string().datetime(),
}).openapi("Agent");

registry.register("Agent", AgentResponseSchema);
```

