### 2. Blocks Feed Component

```tsx
// apps/web/src/components/dcg/blocks-feed.tsx

import { useDCGBlocks } from "@/hooks/dcg";
import { useWebSocket } from "@/hooks/websocket";
import { formatDistanceToNow } from "date-fns";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { motion, AnimatePresence } from "framer-motion";

export function DCGBlocksFeed() {
  const { data: blocks, refetch } = useDCGBlocks({ limit: 50 });
  const [filter, setFilter] = useState<string | null>(null);

  // Real-time updates
  useWebSocket("system:dcg", (event) => {
    if (event.type === "dcg.block") {
      refetch();
    }
  });

  const filteredBlocks = filter
    ? blocks?.filter(b => b.severity === filter || b.pack === filter)
    : blocks;

  return (
    <div className="space-y-4">
      {/* Filters */}
      <div className="flex gap-2">
        <Button
          variant={filter === null ? "default" : "outline"}
          size="sm"
          onClick={() => setFilter(null)}
        >
          All
        </Button>
        {["critical", "high", "medium", "low"].map(severity => (
          <Button
            key={severity}
            variant={filter === severity ? "default" : "outline"}
            size="sm"
            onClick={() => setFilter(severity)}
          >
            <SeverityBadge severity={severity} />
          </Button>
        ))}
      </div>

      {/* Blocks List */}
      <ScrollArea className="h-[600px]">
        <AnimatePresence>
          {filteredBlocks?.map((block) => (
            <motion.div
              key={block.id}
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              className="border rounded-lg p-4 mb-2"
            >
              <div className="flex items-start justify-between">
                <div className="space-y-1">
                  <div className="flex items-center gap-2">
                    <SeverityBadge severity={block.severity} />
                    <Badge variant="outline">{block.pack}</Badge>
                    <span className="text-sm text-muted-foreground">
                      {formatDistanceToNow(new Date(block.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                  <code className="text-sm bg-muted p-1 rounded block truncate max-w-xl">
                    {block.command}
                  </code>
                  <p className="text-sm text-muted-foreground">{block.reason}</p>
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => markFalsePositive(block.id)}
                    disabled={block.falsePositive}
                  >
                    {block.falsePositive ? "Marked FP" : "False Positive"}
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => showDetails(block)}
                  >
                    Details
                  </Button>
                </div>
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
      </ScrollArea>
    </div>
  );
}

function SeverityBadge({ severity }: { severity: string }) {
  const variants: Record<string, string> = {
    critical: "bg-red-500 text-white",
    high: "bg-orange-500 text-white",
    medium: "bg-yellow-500 text-black",
    low: "bg-blue-500 text-white",
  };

  return (
    <Badge className={variants[severity] || "bg-gray-500"}>
      {severity.toUpperCase()}
    </Badge>
  );
}
```

