```tsx
// apps/web/src/components/dcg/pending-list.tsx

import { useDCGPendingExceptions, useApprovePendingException, useDenyPendingException } from "@/hooks/dcg";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";

export function DCGPendingList() {
  const { data: pending, refetch } = useDCGPendingExceptions();
  const approveMutation = useApprovePendingException();
  const denyMutation = useDenyPendingException();
  const [denyDialogOpen, setDenyDialogOpen] = useState(false);
  const [selectedId, setSelectedId] = useState<string | null>(null);
  const [denyReason, setDenyReason] = useState("");

  useWebSocket("system:dcg", (event) => {
    if (event.type.startsWith("dcg.pending_exception")) {
      refetch();
    }
  });

  const handleApprove = async (shortCode: string) => {
    await approveMutation.mutateAsync(shortCode);
    refetch();
  };

  const handleDeny = async () => {
    if (selectedId) {
      await denyMutation.mutateAsync({ shortCode: selectedId, reason: denyReason });
      setDenyDialogOpen(false);
      setDenyReason("");
      refetch();
    }
  };

  if (!pending || pending.length === 0) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center h-48">
          <div className="text-center text-muted-foreground">
            <CheckCircle2 className="h-12 w-12 mx-auto mb-4" />
            <p>No pending exceptions</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {pending.map((exception) => (
        <Card key={exception.id} className="border-l-4 border-l-yellow-500">
          <CardContent className="p-4">
            <div className="flex items-start justify-between">
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <Badge variant="outline" className="font-mono">
                    {exception.shortCode}
                  </Badge>
                  <SeverityBadge severity={exception.severity} />
                  <Badge variant="outline">{exception.pack}</Badge>
                </div>
                <code className="text-sm bg-muted p-2 rounded block">
                  {exception.command}
                </code>
                <p className="text-sm text-muted-foreground">{exception.reason}</p>
                <div className="flex items-center gap-4 text-xs text-muted-foreground">
                  <span>Agent: {exception.agentId || "Unknown"}</span>
                  <span>Expires: {formatDistanceToNow(new Date(exception.expiresAt), { addSuffix: true })}</span>
                </div>
              </div>
              <div className="flex gap-2">
                <Button
                  variant="default"
                  size="sm"
                  onClick={() => handleApprove(exception.shortCode)}
                  disabled={approveMutation.isPending}
                >
                  Approve
                </Button>
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={() => {
                    setSelectedId(exception.shortCode);
                    setDenyDialogOpen(true);
                  }}
                  disabled={denyMutation.isPending}
                >
                  Deny
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      ))}

      <Dialog open={denyDialogOpen} onOpenChange={setDenyDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Deny Exception</DialogTitle>
          </DialogHeader>
          <Textarea
            placeholder="Reason for denial (optional)"
            value={denyReason}
            onChange={(e) => setDenyReason(e.target.value)}
          />
          <DialogFooter>
            <Button variant="outline" onClick={() => setDenyDialogOpen(false)}>
              Cancel
            </Button>
            <Button variant="destructive" onClick={handleDeny}>
              Deny
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}
