// apps/web/src/components/dcg/packs-config.tsx

import { useDCGPacks, useDCGConfig, useEnablePack, useDisablePack } from "@/hooks/dcg";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Badge } from "@/components/ui/badge";

export function DCGPacksConfig() {
  const { data: packs } = useDCGPacks();
  const { data: config } = useDCGConfig();
  const enableMutation = useEnablePack();
  const disableMutation = useDisablePack();

  const isEnabled = (packId: string) => {
    return config?.enabledPacks.includes(packId) && !config?.disabledPacks.includes(packId);
  };

  const handleToggle = async (packId: string, enabled: boolean) => {
    if (enabled) {
      await enableMutation.mutateAsync(packId);
    } else {
      await disableMutation.mutateAsync(packId);
    }
  };

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <CardTitle>Rule Packs Configuration</CardTitle>
        </CardHeader>
        <CardContent>
          <Accordion type="single" collapsible className="w-full">
            {packs?.map((pack) => (
              <AccordionItem key={pack.id} value={pack.id}>
                <AccordionTrigger className="hover:no-underline">
                  <div className="flex items-center justify-between w-full pr-4">
                    <div className="flex items-center gap-3">
                      <Switch
                        checked={isEnabled(pack.id)}
                        onCheckedChange={(checked) => handleToggle(pack.id, checked)}
                        onClick={(e) => e.stopPropagation()}
                      />
                      <span className="font-mono">{pack.id}</span>
                      <Badge variant="secondary">{pack.ruleCount} rules</Badge>
                    </div>
                  </div>
