### 6. Command Tester

```tsx
// apps/web/src/components/dcg/command-tester.tsx

import { useState } from "react";
import { useExplainCommand, useTestCommand } from "@/hooks/dcg";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

export function DCGCommandTester() {
  const [command, setCommand] = useState("");
  const explainMutation = useExplainCommand();
  const testMutation = useTestCommand();

  const handleExplain = async () => {
    if (command.trim()) {
      await explainMutation.mutateAsync(command);
    }
  };

  const handleTest = async () => {
    if (command.trim()) {
      await testMutation.mutateAsync(command);
    }
  };

  const result = explainMutation.data || testMutation.data;

  return (
    <Card>
      <CardHeader>
        <CardTitle>Command Tester</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex gap-2">
          <Input
            placeholder="Enter a command to test..."
            value={command}
            onChange={(e) => setCommand(e.target.value)}
            className="font-mono"
            onKeyDown={(e) => e.key === "Enter" && handleExplain()}
          />
          <Button onClick={handleExplain} disabled={explainMutation.isPending}>
            Explain
          </Button>
          <Button variant="outline" onClick={handleTest} disabled={testMutation.isPending}>
            Test
          </Button>
        </div>

        {result && (
          <div className="border rounded-lg p-4 space-y-4">
            <div className="flex items-center gap-2">
              {result.wouldBlock || result.blocked ? (
                <>
                  <ShieldAlert className="h-5 w-5 text-red-500" />
                  <span className="font-semibold text-red-500">Would be blocked</span>
                </>
              ) : (
                <>
                  <ShieldCheck className="h-5 w-5 text-green-500" />
                  <span className="font-semibold text-green-500">Would be allowed</span>
                </>
              )}
            </div>

            {result.matchedRules && result.matchedRules.length > 0 && (
              <div className="space-y-2">
                <h4 className="font-semibold">Matched Rules:</h4>
                {result.matchedRules.map((rule: any, i: number) => (
                  <div key={i} className="bg-muted p-2 rounded">
                    <div className="flex items-center gap-2">
                      <SeverityBadge severity={rule.severity} />
                      <code className="font-mono">{rule.ruleId}</code>
                    </div>
                    <p className="text-sm text-muted-foreground mt-1">{rule.reason}</p>
                  </div>
                ))}
              </div>
            )}

            {result.safeAlternatives && result.safeAlternatives.length > 0 && (
              <div className="space-y-2">
                <h4 className="font-semibold">Safe Alternatives:</h4>
                <ul className="list-disc list-inside text-sm">
                  {result.safeAlternatives.map((alt: string, i: number) => (
                    <li key={i}>{alt}</li>
                  ))}
                </ul>
              </div>
            )}

            {result.contextClassification && (
              <div className="flex items-center gap-2">
                <span className="text-sm text-muted-foreground">Context:</span>
                <Badge variant="outline">{result.contextClassification}</Badge>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}
