// apps/web/src/hooks/dcg.ts

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";

export function useDCGStats() {
  return useQuery({
    queryKey: ["dcg", "stats"],
    queryFn: () => api.get("/dcg/stats").json<DCGStats>(),
    refetchInterval: 30000, // Refresh every 30 seconds
  });
}

export function useDCGBlocks(options?: { limit?: number; severity?: string }) {
  return useQuery({
    queryKey: ["dcg", "blocks", options],
    queryFn: () => api.get("/dcg/blocks", { searchParams: options }).json<{ blocks: DCGBlock[] }>(),
  });
}

export function useDCGPendingExceptions() {
  return useQuery({
    queryKey: ["dcg", "pending"],
    queryFn: () => api.get("/dcg/pending").json<{ exceptions: PendingException[] }>(),
    refetchInterval: 10000, // Refresh every 10 seconds
  });
