// apps/gateway/src/services/audit-export.service.ts

interface ExportOptions {
  format: 'csv' | 'json' | 'json_lines';
  dateRange: { start: Date; end: Date };
  filters?: AuditFilter;
  includeFields?: string[];
  excludeFields?: string[];
