  actions?: AuditAction[];
  actors?: string[];
  resources?: { type: ResourceType; id?: string }[];
  status?: ('success' | 'failure')[];
  searchQuery?: string;
}

class AuditExportService {
  async exportToFile(options: ExportOptions): Promise<ExportResult> {
    // Stream-based export for large datasets
    const stream = this.createExportStream(options);
    const filename = this.generateFilename(options);

    // Write to temporary file
    const tempPath = await this.writeStreamToFile(stream, filename, options.compression);

    // Generate signed download URL
    const downloadUrl = await this.uploadToStorage(tempPath);

    // Audit the export itself
    await this.auditService.log({
      action: 'data.exported',
      resource: { type: 'export', id: filename },
      metadata: {
        format: options.format,
        dateRange: options.dateRange,
        recordCount: stream.recordCount,
      },
    });

    return {
      filename,
      downloadUrl,
      expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000), // 24 hours
      recordCount: stream.recordCount,
      fileSize: stream.byteCount,
    };
  }
}
