// apps/web/src/routes/fleet/index.tsx

import { createFileRoute } from "@tanstack/react-router";
import { useFleetStats, useFleetRepos, useSyncStatus } from "@/hooks/ru";
import { useFleetEvents, useSyncEvents } from "@/hooks/websocket";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { FleetRepoList } from "@/components/ru/fleet-repo-list";
import { SyncProgressBar } from "@/components/ru/sync-progress-bar";
import { FleetStatsCards } from "@/components/ru/fleet-stats-cards";

export const Route = createFileRoute("/fleet/")({
  component: FleetDashboard,
});

function FleetDashboard() {
  const { data: stats, refetch: refetchStats } = useFleetStats();
  const { data: repos, refetch: refetchRepos } = useFleetRepos();
  const [activeSyncId, setActiveSyncId] = useState<string | null>(null);
  const startSyncMutation = useStartSync();
  const cancelSyncMutation = useCancelSync();

  // Real-time updates
  useFleetEvents((event) => {
    if (event.type === "ru.repo_status_changed") {
      refetchRepos();
      refetchStats();
    }
  });

  useSyncEvents(null, (event) => {
    if (event.type === "ru.sync_started") {
      setActiveSyncId(event.data.sessionId);
    }
    if (event.type === "ru.sync_completed" || event.type === "ru.sync_cancelled") {
      setActiveSyncId(null);
      refetchStats();
      refetchRepos();
    }
  });

  const handleStartSync = async () => {
    const { sessionId } = await startSyncMutation.mutateAsync({});
    setActiveSyncId(sessionId);
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Fleet Dashboard</h1>
        <div className="flex gap-2">
          {activeSyncId ? (
            <Button
              variant="destructive"
              onClick={() => cancelSyncMutation.mutate(activeSyncId)}
            >
              Cancel Sync
            </Button>
          ) : (
            <Button onClick={handleStartSync}>
              <RefreshCw className="h-4 w-4 mr-2" />
              Sync All
            </Button>
          )}
          <Button variant="outline" asChild>
            <Link to="/fleet/sweep">Agent Sweep</Link>
          </Button>
        </div>
      </div>

      {/* Active Sync Progress */}
      {activeSyncId && (
        <SyncProgressBar sessionId={activeSyncId} />
      )}

      {/* Stats Cards */}
      <FleetStatsCards stats={stats} />

      {/* Repo List */}
      <FleetRepoList repos={repos} />
    </div>
  );
}
