// apps/web/src/components/ru/sweep-plan-list.tsx

import { useSweepPlans, useApprovePlan, useRejectPlan, useBulkApprovePlans } from "@/hooks/ru";

interface SweepPlanListProps {
  sessionId: string;
}

export function SweepPlanList({ sessionId }: SweepPlanListProps) {
  const { data: plans } = useSweepPlans(sessionId);
  const approveMutation = useApprovePlan();
  const rejectMutation = useRejectPlan();
  const bulkApproveMutation = useBulkApprovePlans();
  const [selectedPlanIds, setSelectedPlanIds] = useState<string[]>([]);

  const pendingPlans = plans?.filter(p => p.approvalStatus === "pending") || [];

  const handleBulkApprove = async () => {
    await bulkApproveMutation.mutateAsync(selectedPlanIds);
    setSelectedPlanIds([]);
  };

  return (
    <div className="space-y-4">
      {/* Bulk Actions */}
      {pendingPlans.length > 0 && (
        <div className="flex items-center justify-between p-2 bg-yellow-50 rounded">
          <span className="text-sm">
            {selectedPlanIds.length} of {pendingPlans.length} plans selected
          </span>
          <div className="flex gap-2">
            <Button
              size="sm"
              variant="outline"
              onClick={() => setSelectedPlanIds(pendingPlans.map(p => p.id))}
            >
              Select All
            </Button>
            <Button
              size="sm"
              onClick={handleBulkApprove}
              disabled={selectedPlanIds.length === 0 || bulkApproveMutation.isPending}
            >
              Approve Selected
            </Button>
          </div>
        </div>
      )}

      {/* Plan Cards */}
      {plans?.map((plan) => (
        <PlanCard
          key={plan.id}
          plan={plan}
          selected={selectedPlanIds.includes(plan.id)}
          onSelect={(selected) => {
            setSelectedPlanIds(prev =>
              selected ? [...prev, plan.id] : prev.filter(id => id !== plan.id)
            );
          }}
          onApprove={() => approveMutation.mutate(plan.id)}
          onReject={(reason) => rejectMutation.mutate({ planId: plan.id, reason })}
        />
      ))}
    </div>
  );
}

function PlanCard({
  plan,
  selected,
  onSelect,
  onApprove,
  onReject,
}: {
  plan: AgentSweepPlan;
  selected: boolean;
  onSelect: (selected: boolean) => void;
  onApprove: () => void;
  onReject: (reason: string) => void;
}) {
  const [showDetails, setShowDetails] = useState(false);
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const parsedPlan = JSON.parse(plan.planJson);

  const riskColors = {
    low: "bg-green-100 text-green-800",
    medium: "bg-yellow-100 text-yellow-800",
    high: "bg-orange-100 text-orange-800",
    critical: "bg-red-100 text-red-800",
  };

  return (
    <Card className={selected ? "ring-2 ring-blue-500" : ""}>
      <CardContent className="p-4">
        <div className="flex items-start justify-between">
          <div className="flex items-start gap-3">
            {plan.approvalStatus === "pending" && (
              <Checkbox
                checked={selected}
                onCheckedChange={onSelect}
              />
            )}
            <div>
              <div className="flex items-center gap-2">
                <span className="font-medium">{plan.repoFullName}</span>
                <Badge className={riskColors[plan.riskLevel || "low"]}>
                  {plan.riskLevel || "low"} risk
                </Badge>
                <ApprovalBadge status={plan.approvalStatus} />
              </div>
              <p className="text-sm text-muted-foreground mt-1">
                {plan.actionCount} actions: {plan.commitActions} commits, {plan.releaseActions} releases
              </p>
            </div>
          </div>

          {plan.approvalStatus === "pending" && (
            <div className="flex gap-2">
              <Button size="sm" onClick={onApprove}>
                Approve
              </Button>
              <Button size="sm" variant="outline" onClick={() => setRejectDialogOpen(true)}>
                Reject
              </Button>
            </div>
          )}
        </div>

        {/* Expandable Details */}
        <Collapsible open={showDetails} onOpenChange={setShowDetails}>
          <CollapsibleTrigger className="text-sm text-blue-500 mt-2">
            {showDetails ? "Hide details" : "Show details"}
          </CollapsibleTrigger>
          <CollapsibleContent className="mt-2">
            <pre className="bg-muted p-2 rounded text-xs overflow-x-auto">
              {JSON.stringify(parsedPlan, null, 2)}
            </pre>
          </CollapsibleContent>
        </Collapsible>

        {/* Reject Dialog */}
        <RejectDialog
          open={rejectDialogOpen}
          onOpenChange={setRejectDialogOpen}
          onReject={onReject}
        />
      </CardContent>
    </Card>
  );
}
