// apps/gateway/src/utils/links.ts

export interface LinkSet {
  self: string;
  [key: string]: string;
}

export interface LinkGeneratorContext {
  baseUrl: string;
}

export type LinkGenerator<T> = (
  resource: T,
  context: LinkGeneratorContext
) => LinkSet;
