```typescript
/**
 * Extract base URL from Hono context.
 */
export function getLinkContext(c: Context): LinkGeneratorContext {
  const url = new URL(c.req.url);
  return {
    baseUrl: `${url.protocol}//${url.host}`,
  };
}
```

