export function SweepDCGSummary({ sessionId }: SweepDCGSummaryProps) {
  const { data: summary } = useSweepDCGSummary(sessionId);

  if (!summary) return null;

  const hasIssues = summary.blocks > 0 || summary.pending > 0;

  return (
    <Card className={hasIssues ? "border-yellow-500" : "border-green-500"}>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Shield className="h-5 w-5" />
          DCG Safety Status
        </CardTitle>
      </CardHeader>
      <CardContent>
