            <p className="text-2xl font-bold text-gray-500">{summary.denied}</p>
            <p className="text-xs text-muted-foreground">Denied</p>
          </div>
        </div>

        {summary.blockDetails.length > 0 && (
          <div className="mt-4 space-y-2">
            <h4 className="font-semibold">Blocked Commands:</h4>
            {summary.blockDetails.map((block) => (
              <div key={block.id} className="bg-red-50 p-2 rounded text-sm">
                <code className="block truncate">{block.command}</code>
                <span className="text-red-600">{block.reason}</span>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
