);

CREATE TABLE dcg_packs (
  id VARCHAR(255) PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  description TEXT,
  version VARCHAR(50),
  rule_count INTEGER,
  enabled BOOLEAN DEFAULT true,
  priority INTEGER DEFAULT 0,
  scope VARCHAR(50) DEFAULT 'system'
);

CREATE TABLE dcg_block_events (
  id UUID PRIMARY KEY,
  content_hash VARCHAR(64),
  rule_id VARCHAR(255),
  pack_id VARCHAR(255),
  agent_id UUID,
  repository_id UUID,
  blocked_at TIMESTAMP DEFAULT NOW(),
  metadata JSONB
);

CREATE INDEX idx_block_events_time ON dcg_block_events(blocked_at);
CREATE INDEX idx_block_events_pack ON dcg_block_events(pack_id);
CREATE INDEX idx_feedback_status ON dcg_feedback(status);
