```typescript
// apps/gateway/src/services/ru-fleet.service.ts

import { spawn } from "bun";
import { db } from "../db";
import { fleetRepos, fleetSyncOps } from "../db/schema";
import { getHub } from "../ws/hub";
import { logger } from "./logger";
import { generateId, getCorrelationId } from "../utils";

// Types
export interface FleetRepo {
  id: string;
  owner: string;
  name: string;
  fullName: string;
  url: string;
  localPath?: string;
  status: RepoStatus;
  isCloned: boolean;
  lastSyncAt?: Date;
}

export type RepoStatus = "healthy" | "dirty" | "behind" | "ahead" | "diverged" | "unknown";

export interface FleetStats {
  total: number;
  cloned: number;
  healthy: number;
  dirty: number;
  behind: number;
  syncing: number;
}

// Get all repos in fleet
export async function getFleetRepos(options?: {
  status?: RepoStatus;
  group?: string;
  owner?: string;
  limit?: number;
  offset?: number;
}): Promise<{ repos: FleetRepo[]; total: number }> {
  const correlationId = getCorrelationId();
  const startTime = Date.now();

  let query = db.select().from(fleetRepos);

  if (options?.status) {
    query = query.where(eq(fleetRepos.status, options.status));
  }
  if (options?.group) {
    query = query.where(eq(fleetRepos.ruGroup, options.group));
  }
  if (options?.owner) {
    query = query.where(eq(fleetRepos.owner, options.owner));
  }

  const total = await db.select({ count: count() }).from(fleetRepos);
  const repos = await query
    .orderBy(fleetRepos.fullName)
    .limit(options?.limit || 100)
    .offset(options?.offset || 0);

  logger.info({
    correlationId,
    duration_ms: Date.now() - startTime,
    repoCount: repos.length,
    total: total[0]?.count,
  }, "Fetched fleet repos");

  return { repos, total: total[0]?.count || 0 };
}

// Get fleet statistics
export async function getFleetStats(): Promise<FleetStats> {
  const [stats] = await db
    .select({
      total: count(),
      cloned: sql<number>`SUM(CASE WHEN ${fleetRepos.isCloned} THEN 1 ELSE 0 END)`,
      healthy: sql<number>`SUM(CASE WHEN ${fleetRepos.status} = 'healthy' THEN 1 ELSE 0 END)`,
      dirty: sql<number>`SUM(CASE WHEN ${fleetRepos.status} = 'dirty' THEN 1 ELSE 0 END)`,
      behind: sql<number>`SUM(CASE WHEN ${fleetRepos.status} = 'behind' THEN 1 ELSE 0 END)`,
    })
    .from(fleetRepos);

  // Count active sync operations
  const [syncing] = await db
    .select({ count: count() })
    .from(fleetSyncOps)
    .where(eq(fleetSyncOps.status, "running"));

  return {
    total: stats?.total || 0,
    cloned: stats?.cloned || 0,
    healthy: stats?.healthy || 0,
    dirty: stats?.dirty || 0,
    behind: stats?.behind || 0,
    syncing: syncing?.count || 0,
  };
}

// Add repo to fleet
export async function addRepoToFleet(params: {
  owner: string;
  name: string;
  url: string;
  sshUrl?: string;
  group?: string;
}): Promise<FleetRepo> {
  const correlationId = getCorrelationId();

  const repo = {
    id: generateId("repo_"),
    owner: params.owner,
    name: params.name,
    fullName: `${params.owner}/${params.name}`,
    url: params.url,
    sshUrl: params.sshUrl,
    ruGroup: params.group,
    status: "unknown" as RepoStatus,
    isCloned: false,
  };

  await db.insert(fleetRepos).values(repo);

  getHub().publish(
    { kind: "fleet", scope: "repos" },
    { type: "ru.repo_added", data: repo, timestamp: new Date().toISOString() }
  );

  logger.info({ correlationId, repoId: repo.id, fullName: repo.fullName }, "Added repo to fleet");

  return repo;
}

// Remove repo from fleet
export async function removeRepoFromFleet(repoId: string): Promise<void> {
  const repo = await db.select().from(fleetRepos).where(eq(fleetRepos.id, repoId)).get();

  if (!repo) {
    throw new NotFoundError(`Repo not found: ${repoId}`);
  }

  await db.delete(fleetRepos).where(eq(fleetRepos.id, repoId));

  getHub().publish(
    { kind: "fleet", scope: "repos" },
    { type: "ru.repo_removed", data: { repoId, fullName: repo.fullName }, timestamp: new Date().toISOString() }
  );

  logger.info({ repoId, fullName: repo.fullName }, "Removed repo from fleet");
}

// Update repo status
export async function updateRepoStatus(
  repoId: string,
  status: Partial<FleetRepo>
): Promise<void> {
  await db.update(fleetRepos)
    .set({
      ...status,
      updatedAt: sql`(unixepoch())`,
    })
    .where(eq(fleetRepos.id, repoId));

  getHub().publish(
    { kind: "fleet", scope: "repos" },
    { type: "ru.repo_status_changed", data: { repoId, status }, timestamp: new Date().toISOString() }
  );
}
