### 2. Sync Operations Service

```typescript
// apps/gateway/src/services/ru-sync.service.ts

import { spawn, Subprocess } from "bun";
import { db } from "../db";
import { fleetRepos, fleetSyncOps } from "../db/schema";
import { getHub } from "../ws/hub";
import { logger } from "./logger";

// Active sync processes
const activeSyncs = new Map<string, Subprocess>();

export interface SyncOptions {
  parallelism?: number;
  dryRun?: boolean;
  force?: boolean;
  filter?: {
    repos?: string[];
    group?: string;
    owner?: string;
  };
}

export interface SyncProgress {
  total: number;
  completed: number;
  failed: number;
  current?: string;
  operations: SyncOperation[];
}

// Start fleet sync
export async function startFleetSync(
  triggeredBy: string,
  options?: SyncOptions
): Promise<{ sessionId: string }> {
  const correlationId = getCorrelationId();
  const sessionId = generateId("sync_");
  const startTime = Date.now();

  logger.info({
    correlationId,
    sessionId,
    triggeredBy,
    options,
  }, "Starting fleet sync");

  // Get target repos
  let repos: FleetRepo[];
  if (options?.filter?.repos) {
    repos = await db.select().from(fleetRepos)
      .where(inArray(fleetRepos.id, options.filter.repos));
  } else if (options?.filter?.group) {
    repos = await db.select().from(fleetRepos)
      .where(eq(fleetRepos.ruGroup, options.filter.group));
  } else if (options?.filter?.owner) {
    repos = await db.select().from(fleetRepos)
      .where(eq(fleetRepos.owner, options.filter.owner));
  } else {
    repos = await db.select().from(fleetRepos);
  }

  // Create sync operation records
  const operations = repos.map(repo => ({
    id: generateId("syncop_"),
    repoId: repo.id,
    repoFullName: repo.fullName,
    operation: repo.isCloned ? "pull" : "clone",
    status: "pending",
    triggeredBy,
    correlationId: sessionId,
  }));

  await db.insert(fleetSyncOps).values(operations);

  // Publish start event
  getHub().publish(
    { kind: "fleet", scope: "sync" },
    {
      type: "ru.sync_started",
      data: { sessionId, repoCount: repos.length, triggeredBy },
      timestamp: new Date().toISOString(),
    }
  );

  // Start sync process
  runSyncProcess(sessionId, repos, options);

  return { sessionId };
}

// Run the actual sync process
async function runSyncProcess(
  sessionId: string,
  repos: FleetRepo[],
  options?: SyncOptions
): Promise<void> {
  const correlationId = getCorrelationId();
  const parallelism = options?.parallelism || 4;

  let completed = 0;
  let failed = 0;

  // Process repos in batches
  for (let i = 0; i < repos.length; i += parallelism) {
    const batch = repos.slice(i, i + parallelism);

    await Promise.all(batch.map(async (repo) => {
      const opId = `syncop_${repo.id}`;
      const startTime = Date.now();

      try {
        // Update status to running
        await db.update(fleetSyncOps)
          .set({ status: "running", startedAt: sql`(unixepoch())` })
          .where(and(
            eq(fleetSyncOps.repoId, repo.id),
            eq(fleetSyncOps.correlationId, sessionId)
          ));

        getHub().publish(
          { kind: "fleet", scope: "sync" },
          {
            type: "ru.sync_repo_started",
            data: { sessionId, repoId: repo.id, fullName: repo.fullName },
            timestamp: new Date().toISOString(),
          }
        );

        // Run RU sync command
        const operation = repo.isCloned ? "pull" : "clone";
        const args = ["ru", operation === "clone" ? "sync" : "sync", "--json", repo.fullName];
        if (options?.dryRun) args.push("--dry-run");

        const proc = spawn(args, { stdout: "pipe", stderr: "pipe" });
        activeSyncs.set(repo.id, proc);

        const stdout = await new Response(proc.stdout).text();
        const stderr = await new Response(proc.stderr).text();
        const exitCode = await proc.exited;

        activeSyncs.delete(repo.id);

        const duration = Date.now() - startTime;
        const result = exitCode === 0 ? JSON.parse(stdout) : null;

        if (exitCode === 0) {
          // Success
          await db.update(fleetSyncOps)
            .set({
              status: "success",
              completedAt: sql`(unixepoch())`,
              durationMs: duration,
              toCommit: result?.commit,
              commitCount: result?.commits,
              filesChanged: result?.files,
            })
            .where(and(
              eq(fleetSyncOps.repoId, repo.id),
              eq(fleetSyncOps.correlationId, sessionId)
            ));

          // Update repo status
          await updateRepoStatus(repo.id, {
            isCloned: true,
            status: "healthy",
            lastSyncAt: new Date(),
            lastCommit: result?.commit,
          });

          completed++;
        } else {
          // Failed
          await db.update(fleetSyncOps)
            .set({
              status: "failed",
              completedAt: sql`(unixepoch())`,
              durationMs: duration,
              error: stderr || "Unknown error",
            })
            .where(and(
              eq(fleetSyncOps.repoId, repo.id),
              eq(fleetSyncOps.correlationId, sessionId)
            ));

          failed++;
        }

        // Publish progress
        getHub().publish(
          { kind: "fleet", scope: "sync" },
          {
            type: "ru.sync_repo_completed",
            data: {
              sessionId,
              repoId: repo.id,
              fullName: repo.fullName,
              success: exitCode === 0,
              duration,
              completed,
              failed,
              total: repos.length,
            },
            timestamp: new Date().toISOString(),
          }
        );

        logger.info({
          correlationId,
          sessionId,
          repoId: repo.id,
          success: exitCode === 0,
          duration_ms: duration,
        }, "Sync operation completed");

      } catch (error) {
        failed++;
        logger.error({ correlationId, sessionId, repoId: repo.id, error }, "Sync operation failed");
      }
    }));
  }

  // Publish completion
  getHub().publish(
    { kind: "fleet", scope: "sync" },
    {
      type: "ru.sync_completed",
      data: { sessionId, completed, failed, total: repos.length },
      timestamp: new Date().toISOString(),
    }
  );

  logger.info({
    correlationId,
    sessionId,
    completed,
    failed,
    total: repos.length,
  }, "Fleet sync completed");
}

// Cancel sync
export async function cancelSync(sessionId: string): Promise<void> {
  // Cancel active processes
  for (const [repoId, proc] of activeSyncs) {
    proc.kill();
    activeSyncs.delete(repoId);
  }

  // Update pending operations to cancelled
  await db.update(fleetSyncOps)
    .set({ status: "cancelled" })
    .where(and(
      eq(fleetSyncOps.correlationId, sessionId),
      inArray(fleetSyncOps.status, ["pending", "running"])
    ));

  getHub().publish(
    { kind: "fleet", scope: "sync" },
    { type: "ru.sync_cancelled", data: { sessionId }, timestamp: new Date().toISOString() }
  );
}

// Get sync history
export async function getSyncHistory(options?: {
  repoId?: string;
  sessionId?: string;
  limit?: number;
}): Promise<SyncOperation[]> {
  let query = db.select().from(fleetSyncOps);

  if (options?.repoId) {
    query = query.where(eq(fleetSyncOps.repoId, options.repoId));
  }
  if (options?.sessionId) {
    query = query.where(eq(fleetSyncOps.correlationId, options.sessionId));
  }

  return await query
    .orderBy(desc(fleetSyncOps.createdAt))
    .limit(options?.limit || 50);
}
