### 3. Agent Sweep Service

```typescript
// apps/gateway/src/services/ru-sweep.service.ts

import { spawn } from "bun";
import { db } from "../db";
import { agentSweepSessions, agentSweepPlans, agentSweepLogs, fleetRepos } from "../db/schema";
import { getHub } from "../ws/hub";
import { logger } from "./logger";
import { requestSLBApproval } from "./slb.service";

export interface SweepConfig {
  targetRepos: string[] | "*";
  parallelism?: number;
  dryRun?: boolean;
  autoApprove?: boolean;
  phase1Timeout?: number;  // Analysis timeout (default 300s)
  phase2Timeout?: number;  // Planning timeout (default 600s)
  phase3Timeout?: number;  // Execution timeout (default 300s)
}

// Start agent sweep session
export async function startAgentSweep(
  triggeredBy: string,
  config: SweepConfig
): Promise<AgentSweepSession> {
  const correlationId = getCorrelationId();
  const sessionId = generateId("sweep_");

  logger.info({ correlationId, sessionId, triggeredBy, config }, "Starting agent sweep");

  // Determine target repos
  let repos: FleetRepo[];
  if (config.targetRepos === "*") {
    repos = await db.select().from(fleetRepos).where(eq(fleetRepos.isCloned, true));
  } else {
    repos = await db.select().from(fleetRepos)
      .where(inArray(fleetRepos.id, config.targetRepos));
  }

  // Create session
  const session = {
    id: sessionId,
    targetRepos: JSON.stringify(repos.map(r => r.id)),
    repoCount: repos.length,
    config: JSON.stringify(config),
    parallelism: config.parallelism || 1,
    status: "pending",
    slbApprovalRequired: !config.autoApprove,
    triggeredBy,
  };

  await db.insert(agentSweepSessions).values(session);

  // Log session start
  await logSweepEvent(sessionId, "info", "Agent sweep session created", { repoCount: repos.length });

  // Publish event
  getHub().publish(
    { kind: "fleet", scope: "sweep" },
    {
      type: "ru.sweep_created",
      data: { sessionId, repoCount: repos.length, triggeredBy },
      timestamp: new Date().toISOString(),
    }
  );

  // If auto-approve, start immediately; otherwise wait for SLB approval
  if (config.autoApprove) {
    runSweepPhases(sessionId, repos, config);
  } else {
    // Request SLB approval
    const approval = await requestSLBApproval({
      type: "agent-sweep",
      sessionId,
      description: `Agent sweep for ${repos.length} repositories`,
      metadata: { repos: repos.map(r => r.fullName) },
    });

    await db.update(agentSweepSessions)
      .set({ slbApprovalId: approval.id })
      .where(eq(agentSweepSessions.id, sessionId));
  }

  return await db.select().from(agentSweepSessions).where(eq(agentSweepSessions.id, sessionId)).get();
}

// Run all three phases
async function runSweepPhases(
  sessionId: string,
  repos: FleetRepo[],
  config: SweepConfig
): Promise<void> {
  const correlationId = getCorrelationId();

  try {
    // Update to running
    await db.update(agentSweepSessions)
      .set({ status: "running", startedAt: sql`(unixepoch())`, currentPhase: "phase1_analysis" })
      .where(eq(agentSweepSessions.id, sessionId));

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      { type: "ru.sweep_started", data: { sessionId }, timestamp: new Date().toISOString() }
    );

    // Phase 1: Analysis
    await runPhase1Analysis(sessionId, repos, config);

    // Phase 2: Planning
    await runPhase2Planning(sessionId, repos, config);

    // Phase 3: Execution (if not dry run)
    if (!config.dryRun) {
      await runPhase3Execution(sessionId, config);
    }

    // Complete
    await db.update(agentSweepSessions)
      .set({
        status: "completed",
        completedAt: sql`(unixepoch())`,
        totalDurationMs: sql`((unixepoch()) - ${agentSweepSessions.startedAt}) * 1000`,
      })
      .where(eq(agentSweepSessions.id, sessionId));

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      { type: "ru.sweep_completed", data: { sessionId }, timestamp: new Date().toISOString() }
    );

  } catch (error) {
    await db.update(agentSweepSessions)
      .set({ status: "failed" })
      .where(eq(agentSweepSessions.id, sessionId));

    await logSweepEvent(sessionId, "error", "Sweep failed", { error: error.message });

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      { type: "ru.sweep_failed", data: { sessionId, error: error.message }, timestamp: new Date().toISOString() }
    );

    logger.error({ correlationId, sessionId, error }, "Agent sweep failed");
  }
}

// Phase 1: Analysis
async function runPhase1Analysis(
  sessionId: string,
  repos: FleetRepo[],
  config: SweepConfig
): Promise<void> {
  await logSweepEvent(sessionId, "info", "Starting Phase 1: Analysis");

  let analyzed = 0;
  const timeout = config.phase1Timeout || 300;

  for (const repo of repos) {
    const startTime = Date.now();

    // Run ru agent-sweep --phase1
    const proc = spawn(
      ["ru", "agent-sweep", "--phase", "1", "--json", "--timeout", String(timeout), repo.fullName],
      { stdout: "pipe", stderr: "pipe" }
    );

    const stdout = await new Response(proc.stdout).text();
    const exitCode = await proc.exited;

    if (exitCode === 0) {
      const result = JSON.parse(stdout);
      await logSweepEvent(sessionId, "info", `Analyzed ${repo.fullName}`, {
        duration_ms: Date.now() - startTime,
        findings: result.findings,
      });
      analyzed++;
    } else {
      await logSweepEvent(sessionId, "warn", `Analysis failed for ${repo.fullName}`);
    }

    // Update progress
    await db.update(agentSweepSessions)
      .set({ reposAnalyzed: analyzed })
      .where(eq(agentSweepSessions.id, sessionId));

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      {
        type: "ru.sweep_progress",
        data: { sessionId, phase: "phase1", analyzed, total: repos.length },
        timestamp: new Date().toISOString(),
      }
    );
  }

  await db.update(agentSweepSessions)
    .set({
      currentPhase: "phase2_planning",
      phase1CompletedAt: sql`(unixepoch())`,
    })
    .where(eq(agentSweepSessions.id, sessionId));

  await logSweepEvent(sessionId, "info", "Phase 1 completed", { analyzed });
}

// Phase 2: Planning
async function runPhase2Planning(
  sessionId: string,
  repos: FleetRepo[],
  config: SweepConfig
): Promise<void> {
  await logSweepEvent(sessionId, "info", "Starting Phase 2: Planning");

  let planned = 0;
  const timeout = config.phase2Timeout || 600;

  for (const repo of repos) {
    // Run ru agent-sweep --phase2
    const proc = spawn(
      ["ru", "agent-sweep", "--phase", "2", "--json", "--timeout", String(timeout), repo.fullName],
      { stdout: "pipe", stderr: "pipe" }
    );

    const stdout = await new Response(proc.stdout).text();
    const exitCode = await proc.exited;

    if (exitCode === 0) {
      const plan = JSON.parse(stdout);

      // Store plan
      const planRecord = {
        id: generateId("plan_"),
        sessionId,
        repoId: repo.id,
        repoFullName: repo.fullName,
        planJson: JSON.stringify(plan),
        actionCount: plan.actions?.length || 0,
        riskLevel: assessRiskLevel(plan),
        commitActions: plan.actions?.filter(a => a.type === "commit").length || 0,
        releaseActions: plan.actions?.filter(a => a.type === "release").length || 0,
        branchActions: plan.actions?.filter(a => a.type === "branch").length || 0,
        prActions: plan.actions?.filter(a => a.type === "pr").length || 0,
        approvalStatus: "pending",
      };

      await db.insert(agentSweepPlans).values(planRecord);
      planned++;

      await logSweepEvent(sessionId, "info", `Created plan for ${repo.fullName}`, {
        planId: planRecord.id,
        actionCount: planRecord.actionCount,
        riskLevel: planRecord.riskLevel,
      });
    }

    // Update progress
    await db.update(agentSweepSessions)
      .set({ reposPlanned: planned })
      .where(eq(agentSweepSessions.id, sessionId));

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      {
        type: "ru.sweep_progress",
        data: { sessionId, phase: "phase2", planned, total: repos.length },
        timestamp: new Date().toISOString(),
      }
    );
  }

  await db.update(agentSweepSessions)
    .set({
      currentPhase: "phase3_execution",
      phase2CompletedAt: sql`(unixepoch())`,
    })
    .where(eq(agentSweepSessions.id, sessionId));

  await logSweepEvent(sessionId, "info", "Phase 2 completed", { planned });
}

// Phase 3: Execution
async function runPhase3Execution(
  sessionId: string,
  config: SweepConfig
): Promise<void> {
  await logSweepEvent(sessionId, "info", "Starting Phase 3: Execution");

  // Get approved plans
  const plans = await db.select()
    .from(agentSweepPlans)
    .where(and(
      eq(agentSweepPlans.sessionId, sessionId),
      eq(agentSweepPlans.approvalStatus, "approved")
    ));

  let executed = 0;
  let failed = 0;
  const timeout = config.phase3Timeout || 300;

  for (const plan of plans) {
    const startTime = Date.now();

    // Update plan status
    await db.update(agentSweepPlans)
      .set({ executionStatus: "running" })
      .where(eq(agentSweepPlans.id, plan.id));

    // Run ru agent-sweep --phase3
    const proc = spawn(
      ["ru", "agent-sweep", "--phase", "3", "--json", "--timeout", String(timeout), "--plan", plan.planJson],
      { stdout: "pipe", stderr: "pipe" }
    );

    const stdout = await new Response(proc.stdout).text();
    const exitCode = await proc.exited;

    const result = exitCode === 0 ? JSON.parse(stdout) : { error: stdout };

    await db.update(agentSweepPlans)
      .set({
        executionStatus: exitCode === 0 ? "completed" : "failed",
        executedAt: sql`(unixepoch())`,
        executionResult: JSON.stringify(result),
      })
      .where(eq(agentSweepPlans.id, plan.id));

    if (exitCode === 0) {
      executed++;
    } else {
      failed++;
    }

    await logSweepEvent(sessionId, exitCode === 0 ? "info" : "error",
      `Executed plan for ${plan.repoFullName}`, {
        planId: plan.id,
        success: exitCode === 0,
        duration_ms: Date.now() - startTime,
      });

    // Update session progress
    await db.update(agentSweepSessions)
      .set({ reposExecuted: executed, reposFailed: failed })
      .where(eq(agentSweepSessions.id, sessionId));

    getHub().publish(
      { kind: "fleet", scope: "sweep" },
      {
        type: "ru.sweep_progress",
        data: { sessionId, phase: "phase3", executed, failed, total: plans.length },
        timestamp: new Date().toISOString(),
      }
    );
  }

  await db.update(agentSweepSessions)
    .set({ phase3CompletedAt: sql`(unixepoch())` })
    .where(eq(agentSweepSessions.id, sessionId));

  await logSweepEvent(sessionId, "info", "Phase 3 completed", { executed, failed });
}

// Helper: Log sweep event
async function logSweepEvent(
  sessionId: string,
  level: string,
  message: string,
  data?: Record<string, unknown>
): Promise<void> {
  await db.insert(agentSweepLogs).values({
    id: generateId("log_"),
    sessionId,
    phase: "sweep",
    level,
    message,
    data: data ? JSON.stringify(data) : null,
  });

  logger.info({ sessionId, level, message, data }, "Sweep event");
}

// Helper: Assess risk level
function assessRiskLevel(plan: unknown): "low" | "medium" | "high" | "critical" {
  const actions = (plan as any).actions || [];
  const hasRelease = actions.some(a => a.type === "release");
  const hasDestructive = actions.some(a => a.destructive);
  const actionCount = actions.length;

  if (hasDestructive) return "critical";
  if (hasRelease && actionCount > 5) return "high";
  if (hasRelease || actionCount > 10) return "medium";
  return "low";
}

// Approve plan
export async function approveSweepPlan(
  planId: string,
  approvedBy: string
): Promise<void> {
  await db.update(agentSweepPlans)
    .set({
      approvalStatus: "approved",
      approvedBy,
      approvedAt: sql`(unixepoch())`,
    })
    .where(eq(agentSweepPlans.id, planId));

  logger.info({ planId, approvedBy }, "Sweep plan approved");
}

// Reject plan
export async function rejectSweepPlan(
  planId: string,
  rejectedBy: string,
  reason: string
): Promise<void> {
  await db.update(agentSweepPlans)
    .set({
      approvalStatus: "rejected",
      rejectedReason: reason,
    })
    .where(eq(agentSweepPlans.id, planId));

  logger.info({ planId, rejectedBy, reason }, "Sweep plan rejected");
}

// Get session with plans
export async function getSweepSession(sessionId: string): Promise<SweepSessionWithPlans> {
  const session = await db.select()
    .from(agentSweepSessions)
    .where(eq(agentSweepSessions.id, sessionId))
    .get();

  if (!session) {
    throw new NotFoundError(`Sweep session not found: ${sessionId}`);
  }

  const plans = await db.select()
    .from(agentSweepPlans)
    .where(eq(agentSweepPlans.sessionId, sessionId));

  const logs = await db.select()
    .from(agentSweepLogs)
    .where(eq(agentSweepLogs.sessionId, sessionId))
    .orderBy(desc(agentSweepLogs.timestamp))
    .limit(100);

  return { ...session, plans, logs };
}
