### 2. WebSocket Channel Configuration

```typescript
// apps/gateway/src/ws/channels.ts

export const RU_CHANNELS = {
  // Fleet-wide updates (repo add/remove/status)
  FLEET: { kind: "fleet", scope: "repos" },

  // Sync operation updates
  SYNC: { kind: "fleet", scope: "sync" },

  // Specific sync session
  SYNC_SESSION: (sessionId: string) => ({
    kind: "fleet",
    scope: `sync:${sessionId}`,
  }),

  // Sweep updates
  SWEEP: { kind: "fleet", scope: "sweep" },

  // Specific sweep session
  SWEEP_SESSION: (sessionId: string) => ({
    kind: "fleet",
    scope: `sweep:${sessionId}`,
  }),
};

// Event routing helper
export function getChannelForEvent(event: RUEvent): Channel {
  switch (event.type) {
    case "ru.repo_added":
    case "ru.repo_removed":
    case "ru.repo_status_changed":
      return RU_CHANNELS.FLEET;

    case "ru.sync_started":
    case "ru.sync_completed":
    case "ru.sync_cancelled":
      return RU_CHANNELS.SYNC;

    case "ru.sync_repo_started":
    case "ru.sync_repo_completed":
      return RU_CHANNELS.SYNC_SESSION(event.data.sessionId);

    case "ru.sweep_created":
    case "ru.sweep_started":
    case "ru.sweep_completed":
      return RU_CHANNELS.SWEEP;

    case "ru.sweep_progress":
    case "ru.sweep_phase_completed":
    case "ru.sweep_plan_created":
    case "ru.sweep_plan_approved":
    case "ru.sweep_plan_rejected":
      return RU_CHANNELS.SWEEP_SESSION(event.data.sessionId);

    default:
      return RU_CHANNELS.FLEET;
  }
}
