// apps/gateway/src/services/ru-events.ts

import { getHub } from "../ws/hub";
import { getChannelForEvent, RU_CHANNELS } from "../ws/channels";
import { logger } from "./logger";

export function publishRUEvent(event: RUEvent): void {
  const channel = getChannelForEvent(event);
  const eventWithTimestamp = {
    ...event,
    timestamp: event.timestamp || new Date().toISOString(),
  };

  getHub().publish(channel, eventWithTimestamp);

  logger.debug({
    eventType: event.type,
    channel,
    sessionId: event.data.sessionId,
  }, "Published RU event");
}

