### 4. WebSocket Handler Updates

```typescript
// apps/gateway/src/ws/handler.ts

import { RU_CHANNELS } from "./channels";

// Add RU channel subscriptions to handler
export function handleSubscription(ws: WebSocket, message: SubscribeMessage): void {
  const { channel } = message;

  // Validate channel
  const validChannels = [
    "fleet:repos",
    "fleet:sync",
    "fleet:sweep",
    /^fleet:sync:[a-z0-9_]+$/,
    /^fleet:sweep:[a-z0-9_]+$/,
  ];

  const isValid = validChannels.some(pattern =>
    typeof pattern === "string"
      ? pattern === channel
      : pattern.test(channel)
  );

  if (!isValid) {
    ws.send(JSON.stringify({
      type: "error",
      error: "Invalid channel",
      channel,
    }));
    return;
  }

  // Subscribe
  const [kind, scope] = channel.split(":");
  getHub().subscribe(ws, { kind, scope });

  ws.send(JSON.stringify({
    type: "subscribed",
    channel,
  }));

  logger.debug({ channel }, "WebSocket subscribed to RU channel");
}
