export function useSweepEvents(
  sessionId?: string,
  onEvent: (event: RUEvent) => void
): void {
  const channel = sessionId ? `fleet:sweep:${sessionId}` : "fleet:sweep";
  useWebSocketSubscription(channel, onEvent);
}

// Hook for sync progress
export function useSyncProgress(sessionId: string): SyncProgress {
  const [progress, setProgress] = useState<SyncProgress>({
    total: 0,
    completed: 0,
    failed: 0,
    current: undefined,
    operations: [],
  });

  useSyncEvents(sessionId, (event) => {
    switch (event.type) {
      case "ru.sync_repo_started":
        setProgress(p => ({ ...p, current: event.data.fullName }));
        break;
      case "ru.sync_repo_completed":
        setProgress(p => ({
          ...p,
          completed: event.data.completed,
          failed: event.data.failed,
          total: event.data.total,
          current: undefined,
        }));
        break;
      case "ru.sync_completed":
        setProgress(p => ({ ...p, current: undefined }));
        break;
    }
  });

  return progress;
}

// Hook for sweep progress
export function useSweepProgress(sessionId: string): SweepProgress {
  const [progress, setProgress] = useState<SweepProgress>({
    phase: undefined,
    analyzed: 0,
    planned: 0,
    executed: 0,
    failed: 0,
    total: 0,
    current: undefined,
    pendingApprovals: 0,
  });

  useSweepEvents(sessionId, (event) => {
    switch (event.type) {
      case "ru.sweep_progress":
        setProgress(p => ({
          ...p,
          phase: event.data.phase,
          current: event.data.current,
          ...event.data.progress,
        }));
        break;
      case "ru.sweep_plan_created":
        setProgress(p => ({ ...p, pendingApprovals: p.pendingApprovals + 1 }));
        break;
      case "ru.sweep_plan_approved":
      case "ru.sweep_plan_rejected":
        setProgress(p => ({ ...p, pendingApprovals: Math.max(0, p.pendingApprovals - 1) }));
        break;
    }
  });

  return progress;
}
```

