}

type AlertType =
  | 'agent_error'
  | 'agent_stalled'
  | 'conflict_detected'
  | 'reservation_expired'
  | 'daemon_failed'
  | 'quota_warning'
  | 'quota_exceeded'
  | 'approval_required'
  | 'security_violation'
  | 'system_health';

interface AlertRule {
  name: string;
  enabled: boolean;
  condition: (context: AlertContext) => boolean;
  severity: AlertSeverity;
