## 20. Pipeline & Workflow Engine

The Pipeline Engine enables multi-step, multi-agent workflows with conditional logic and parallel execution.

### 20.1 Pipeline Data Model

```typescript
// packages/shared/src/types/pipeline.ts

interface Pipeline {
  id: string;
  name: string;
  description?: string;
  version: number;

  // Pipeline definition
  steps: PipelineStep[];
  variables: Record<string, PipelineVariable>;
  triggers?: PipelineTrigger[];

  // Metadata
  createdAt: Date;
  updatedAt: Date;
  createdBy: string;
  tags: string[];
}

type PipelineStep =
  | AgentStep
  | ParallelStep
  | ConditionalStep
  | LoopStep
  | WaitStep
  | ApprovalStep;

interface AgentStep {
  type: 'agent';
  id: string;
  name: string;
  agentConfig: {
    model: 'claude' | 'codex' | 'gemini';
    driver?: 'sdk' | 'acp' | 'tmux';
    prompt: string;  // Can include ${variable} references
    contextPackConfig?: ContextPackConfig;
  };
  timeout?: number;
  retries?: number;
  onSuccess?: string;  // Step ID to jump to
  onFailure?: string;  // Step ID to jump to
}

interface ParallelStep {
  type: 'parallel';
  id: string;
  name: string;
  branches: PipelineStep[][];
  joinMode: 'all' | 'any' | 'first';  // Wait for all, any, or first
}

interface ConditionalStep {
  type: 'conditional';
  id: string;
  name: string;
  condition: string;  // JavaScript expression
  ifTrue: PipelineStep[];
  ifFalse?: PipelineStep[];
}

interface LoopStep {
  type: 'loop';
  id: string;
  name: string;
  items: string;  // Variable name or expression
  body: PipelineStep[];
  maxIterations?: number;
}

interface WaitStep {
  type: 'wait';
  id: string;
  name: string;
  duration?: number;
  until?: string;  // Expression that must become true
  timeout?: number;
}

interface ApprovalStep {
  type: 'approval';
  id: string;
  name: string;
  message: string;
  approvers?: string[];
  timeout?: number;
  autoApprove?: boolean;  // For testing
}

interface PipelineRun {
  id: string;
  pipelineId: string;
  pipelineVersion: number;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'cancelled';
  startedAt?: Date;
  completedAt?: Date;
  currentStepId?: string;
  variables: Record<string, unknown>;
  stepResults: Record<string, StepResult>;
  error?: string;
}

interface StepResult {
  stepId: string;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'skipped';
  startedAt?: Date;
  completedAt?: Date;
  output?: unknown;
  error?: string;
  agentId?: string;  // For agent steps
}
