    channels: ('in_app' | 'email' | 'slack' | 'webhook')[];
    minPriority: 'low' | 'normal' | 'high' | 'urgent';
  }>;

  digest: {
    enabled: boolean;
    frequency: 'daily' | 'weekly';
    timeOfDay: string;
